mapper.update:

update tblboard 
set subject=#{subject}, content=#{content} 
where num=#{num}


update tblboard 
set readCnt = readCnt+1
where num = #{num}


update tblboard 
set depth= depth+1 
where ref = #{ref} and depth > #{depth}

mapper.select:

select * from commentsBoard
where board_num = #{num}
order by co_num asc
